INSERT INTO project_project VALUES(1,'Бизнес-проекты','Бизнес-проекты','Бизнес-долбоорлор','Business projects',replace(replace('Готовы привлечь инвестиции для вашего бизнес-проекта? Участвуйте в инвестиционном форуме BIF 2024 и представьте свой бизнес перед ведущими инвесторами. Получите шанс на финансирование до 4,5 миллионов долларов!\r\n\r\nВ этом году будет отобраны 100 бизнес-проектов для размещения в каталоге BIF 2024, из которых 15 лучших смогут выступить на форуме с презентацией.','\r',char(13)),'\n',char(10)),replace(replace('Готовы привлечь инвестиции для вашего бизнес-проекта? Участвуйте в инвестиционном форуме BIF 2024 и представьте свой бизнес перед ведущими инвесторами. Получите шанс на финансирование до 4,5 миллионов долларов!\r\n\r\nВ этом году будет отобраны 100 бизнес-проектов для размещения в каталоге BIF 2024, из которых 15 лучших смогут выступить на форуме с презентацией.','\r',char(13)),'\n',char(10)),replace(replace('Сиз бизнес-долбооруңузга инвестиция тартууга даярсызбы?\r\nBIF 2024 форумуна катышып, ишкердигиңизди алдыңкы инвесторлорго тартуулап, 4,5 миллион долларга чейин каржылоо мүмкүнчүлүгүн алыңыз!\r\n\r\nБыйыл BIF 2024 каталогуна киргизүү үчүн 100 бизнес-долбоор тандалат, алардын ичинен эң мыкты 15 долбоор форумда презентация жасай алат.','\r',char(13)),'\n',char(10)),replace(replace('Ready to attract investment for your business project? Participate in the BIF 2024 Investment Forum and present your business in front of leading investors. Get a chance for funding up to 4.5 million dollars!\r\n\r\nThis year, 100 business projects will be selected for placement in the BIF 2024 catalog, of which 15 best will be able to make a presentation at the forum.','\r',char(13)),'\n',char(10)));
